mapper.select:
select * from ( select sys_notice.id as noticeId, sys_notice.*, sys_notice_type.notice_type_name, (select count(*) from sys_notice_view_record where sys_notice_view_record.user_id = #{userId} and sys_notice_view_record.notice_id = sys_notice.id and sys_notice_view_record.del_flag = 0) as viewFlag from sys_notice left join sys_notice_type on sys_notice.notice_type_id = sys_notice_type.id
sys_notice.del_flag = 0 and sys_notice_type.del_flag = 0
and sys_notice.notice_id in (select sys_notice_visible_range.notice_id from sys_notice_visible_range where (sys_notice_visible_range.data_type = #{departmentDataType}
and sys_notice_visible_range.data_id in
#{item}
) or ( sys_notice_visible_range.data_type = #{userCode} and sys_notice_visible_range.data_id = #{userId} ) )
and sys_notice.all_visible_flag = true and sys_notice.publish_time < now()
AND sys_notice_type.id = #{query.noticeTypeId}
AND ( INSTR(sys_notice.title,#{query.keywords}) OR INSTR(sys_notice.author,#{query.keywords}) OR INSTR(sys_notice.documentNumber,#{query.keywords}) OR INSTR(sys_notice.source,#{query.keywords}) )
AND DATE_FORMAT(sys_notice.publish_time, '%Y-%m-%d') >= DATE_FORMAT(#{query.publishTimeBegin}, '%Y-%m-%d')
AND DATE_FORMAT(sys_notice.publish_time, '%Y-%m-%d') <= DATE_FORMAT(#{query.publishTimeEnd}, '%Y-%m-%d')
) t where viewFlag = 0 order by t.publish_time desc
